import React from 'react';

class Shipment extends React.Component {
  render () {
    const {total} = this.props;
    const shipping = total>0 && total<500 ? 350 : 99;
    const shippingNeon = shipping === 99 ?(
      <span className='font-effect-neon total_wrap_cheap'>
        {shipping} ₽
      </span>
    ) : (
      <span>{shipping} ₽</span>
    )

    return(
      <div className='total'>
					<div className='total_wrap'>
            <div>
              <div>
                Доставка: {total>0 ? shippingNeon : null}
                <div className='total_wrap-free'>
                  {total<500 ? `Закажите еще на ${500-total} ₽ для доставки за 99  ₽`
                  : null}
                </div>
              </div>
            </div>
						<div className='total_wrap_final'>
							Итого: {total} ₽
						</div>
					</div>
				</div>
    );
  }
}

export default Shipment;
